import React, { useEffect, useState } from "react";
import { Badge, Button, Modal, Table } from "react-bootstrap";
import { useSelector } from "react-redux";
import axios from "../axios";
import Loading from "./Loading";
import Pagination from "./Pagination";

function OrdersAdminPage() {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(false);
    const [orderToShow, setOrderToShow] = useState([]);
    const [show, setShow] = useState(false);
    
    useEffect(() => {
        setLoading(true);
        axios
            .get("/orders")
            .then(({ data }) => {
                setLoading(false);
                setOrders(data);
            })
            .catch((e) => {
                setLoading(false);
                console.error("Error fetching orders:", e);
            });
    }, []);

    const handleShowOrder = (order) => {
        console.log("Showing order:", order);
        setOrderToShow(order.items);
        setShow(true);
    };

    if (loading) {
        return <Loading />;
    }

    if (orders.length === 0) {
        return <h1 className="text-center pt-4">No orders yet</h1>;
    }

    return (
        <>
            <Table responsive striped bordered hover>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Client Name</th>
                        <th>Items</th>
                        <th>Order Total</th>
                        <th>Address</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.map((order) => (
                        <TableRow key={order._id} order={order} handleShowOrder={handleShowOrder} />
                    ))}
                </tbody>
            </Table>

            <Modal show={show} onHide={() => setShow(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Order details</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {orderToShow.map((product, index) => (
                        <div key={index} className="order-details__container d-flex justify-content-around py-2">
                            <img src={product.image} alt={product.name} style={{ maxWidth: 100, height: 100, objectFit: "cover" }} />
                            <p>
                                <span>{product.quantity} x </span> {product.name}
                            </p>
                            <p>Price: ${product.price}</p>
                        </div>
                    ))}
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShow(false)}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

function TableRow({ order, handleShowOrder }) {
    const { _id, owner, items, total, address } = order;
    return (
        <tr>
            <td>{_id}</td>
            <td>{owner?.name}</td>
            <td>
                <Button size="sm" onClick={() => handleShowOrder(order)}>
                    View items
                </Button>
            </td>
            <td>{total}</td>
            <td>{address}</td>
        </tr>
    );
}

export default OrdersAdminPage;
