import React, { useEffect, useState } from "react";
import { Badge, Container, Table } from "react-bootstrap";
import { useSelector } from "react-redux";
import axios from "../axios";
import Loading from "../components/Loading";
import "./OrdersPage.css";
import TableRow from "./TableRow"; 

function OrdersPage() {
    const user = useSelector((state) => state.user);
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(false);
    const [productsObj, setProductsObj] = useState(null);

    // Fetch or define productsObj
    useEffect(() => {
        // Fetch or define productsObj
        const fetchData = async () => {
            try {
                const response = await fetch("/api/productsObj"); // Adjust the endpoint accordingly
                const data = await response.json();
                setProductsObj(data); // Set productsObj state with fetched data
            } catch (error) {
                console.error("Error fetching productsObj:", error);
            }
        };

        fetchData(); // Call fetchData function to fetch productsObj
    }, []); 
    useEffect(() => {
        setLoading(true);
        axios
            .get(`/orders?userId=${user._id}`)
            .then(({ data }) => {
                setLoading(false);
                setOrders(data);
            })
            .catch((error) => {
                setLoading(false);
                console.log(error);
            });
    }, [user._id]);

    if (loading) {
        return <Loading />;
    }

    if (orders.length === 0) {
        return <h1 className="text-center pt-3">No orders yet</h1>;
    }

    return (
        <Container>
            <h1 className="text-center">Your orders</h1>
            <Table responsive striped bordered hover>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Status</th>
                        <th>Date</th>
                        <th>Total</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.map((order) => (
                        <tr key={order._id}>
                            <td>{order._id}</td>
                            <td>
                                <Badge bg={`${order.status === "processing" ? "warning" : "success"}`} text="white">
                                    {order.status}
                                </Badge>
                            </td>
                            <td>{order.date}</td>
                            <td>${order.total}</td>
                        </tr>
                    ))}
                </tbody>
                 <TableRow productsObj={productsObj} />
            </Table>
        </Container>
    );
}

export default OrdersPage;
