    import React, { useEffect, useState } from "react";
    import { Badge, Button, Modal, Table } from "react-bootstrap";
    import { useSelector } from "react-redux";
    import axios from "../axios";
    import Loading from "./Loading";
    import Pagination from "./Pagination";
  

    function OrdersAdminPage() {
        const [orders, setOrders] = useState([]);
        const [loading, setLoading] = useState(false);
        const products = useSelector((state) => state.products);
        const [orderToShow, setOrderToShow] = useState([]);
        const [show, setShow] = useState(false);
        const handleClose = () => setShow(false);
   
        const [productsObj, setProductsObj] = useState(null); // Initialize as null until data is fetched

        useEffect(() => {
            setLoading(true);
            // Assuming you have an endpoint to fetch productsObj
            axios.get("/productsObj")
                .then(response => {
                    setProductsObj(response.data);
                    setLoading(false);
                })
                .catch(error => {
                    console.error("Error fetching productsObj:", error);
                    setLoading(false);
                });
        }, []); 
        function markShipped(orderId, ownerId) {
            axios
                .patch(`/orders/${orderId}/mark-shipped`, { ownerId })
                .then(({ data }) => setOrders(data))
                .catch((e) => console.log(e));
        }
        function showOrder(productsObj) {
            if (!productsObj) {
                console.error("Products object is undefined");
                return;
            }
        
            // Filter products based on their IDs
            let productsToShow = products.filter((product) => productsObj[product._id]);
        
            // Ensure productsToShow is not empty before proceeding
            if (productsToShow.length === 0) {
                console.error("No matching products found");
                return;
            }
        
            // Modify the productsToShow as needed
            productsToShow = productsToShow.map((product) => {
                const productCopy = { ...product };
                productCopy.count = productsObj[product._id];
                delete productCopy.description;
                return productCopy;
            });
        
            // Display the modal and set the order to show
            setShow(true);
            setOrderToShow(productsToShow);
        }

        useEffect(() => {
            setLoading(true);
            axios
                .get("/orders")
                .then(({ data }) => {
                    setLoading(false);
                    setOrders(data);
                })
                .catch((e) => {
                    setLoading(false);
                });
        }, []);

        if (loading) {
            return <Loading />;
        }

        if (orders.length === 0) {
            return <h1 className="text-center pt-4">No orders yet</h1>;
        }

        function TableRow({ _id, count, owner, total, status, products, address, productsObj }) {
            return (
                <tr>
                    <td>{_id}</td>
                    <td>{owner?.name}</td>
                    <td>{count}</td>
                    <td>{total}</td>
                    <td>{address}</td>
                    <td>
                        {status === "processing" ? (
                            <Button size="sm" onClick={() => markShipped(_id, owner?._id)}>
                                Mark as shipped
                            </Button>
                        ) : (
                            <Badge bg="success">Shipped</Badge>
                        )}
                    </td>
                    <td>
                        <span style={{ cursor: "pointer" }} onClick={() => productsObj && showOrder(productsObj)}>
                            View order <i className="fa fa-eye"></i>
                        </span>
                    </td>
                </tr>
            );
        }

        return (
            <>
                <Table responsive striped bordered hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Client Name</th>
                            <th>Items</th>
                            <th>Order Total</th>
                            <th>Address</th>
                        </tr>
                    </thead>
                    <tbody>
                    <Pagination data={orders} RenderComponent={(rowData) => <TableRow {...rowData} productsObj={productsObj} />} pageLimit={1} dataLimit={10} tablePagination={true} />

                    </tbody>
                </Table>

                <Modal show={show} onHide={handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Order details</Modal.Title>
                    </Modal.Header>
                    {orderToShow.map((order) => (
                        <div className="order-details__container d-flex justify-content-around py-2">
                            <img src={order.pictures[0].url} style={{ maxWidth: 100, height: 100, objectFit: "cover" }} />
                            <p>
                                <span>{order.count} x </span> {order.name}
                            </p>
                            <p>Price: ${Number(order.price) * order.count}</p>
                        </div>
                    ))}
                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                    </Modal.Footer>
                </Modal>
            </>
        );
    }

    export default OrdersAdminPage;
