import React, { useEffect, useState } from "react";
import { Badge, Button, Modal, Table } from "react-bootstrap";
import { useSelector } from "react-redux";
import axios from "../axios";
import Loading from "./Loading";
import Pagination from "./Pagination";

function OrdersAdminPage() {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(false);
    const products = useSelector((state) => state.products);
    const [orderToShow, setOrderToShow] = useState([]);
    const [show, setShow] = useState(false);
    const [selectedOrderId, setSelectedOrderId] = useState(null); // State to store the selected order ID
    
    const handleClose = () => setShow(false);

    function markShipped(orderId, ownerId) {
        axios
            .patch(`/orders/${orderId}/mark-shipped`, { ownerId })
            .then(({ data }) => setOrders(data))
            .catch((e) => console.log(e));
    }

    function showOrder(order) {
        // const selectedOrder = orders.find(order => order._id === orderId);
        // if (!selectedOrder) {
        //     console.error("Selected order not found");
        //     return;
        // }
        const { products } = order;
        setShow(true);
        setOrderToShow(products);

        // // Set the selected order to show
        // setOrderToShow(selectedOrder);
        // setShow(true); // Show the modal
    }

    useEffect(() => {
        setLoading(true);
        axios
            .get("/orders")
            .then(({ data }) => {
                setLoading(false);
                setOrders(data);
            })
            .catch((e) => {
                setLoading(false);
            });
    }, []);

    if (loading) {
        return <Loading />;
    }

    if (orders.length === 0) {
        return <h1 className="text-center pt-4">No orders yet</h1>;
    }

    function TableRow({ _id, count, owner, total, status, address }) {
        return (
            <tr key={_id}>
                <td>{_id}</td>
                <td>{owner?.name}</td>
                <td>{count}</td>
                <td>{total}</td>
                <td>{address}</td>
                <td>
                    {status === "processing" ? (
                        <Button size="sm" onClick={() => markShipped(_id, owner?._id)}>
                            Mark as shipped
                        </Button>
                    ) : (
                        <Badge bg="success">Shipped</Badge>
                    )}
                </td>
                <td>
                    <span
                        style={{ cursor: "pointer" }}
                        onClick={() => showOrder(_id)} // Pass the order ID to showOrder function
                    >
                        View order <i className="fa fa-eye"></i>
                    </span>
                </td>
            </tr>
        );
    }

    return (
        <>
            <Table responsive striped bordered hover>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Client Name</th>
                        <th>Items</th>
                        <th>Order Total</th>
                        <th>Address</th>
                    </tr>
                </thead>
                <tbody>
                    <Pagination data={orders} RenderComponent={TableRow} pageLimit={1} dataLimit={10} tablePagination={true} />
                </tbody>
            </Table>

            <Modal show={show} onHide={handleClose}>
    <Modal.Header closeButton>
        <Modal.Title>Order details</Modal.Title>
    </Modal.Header>
    <Modal.Body>
        {orderToShow.map((product) => (
            <div key={product._id} className="order-details__container d-flex justify-content-around py-2">
                <img src={product.image} alt={product.name} style={{ maxWidth: 100, height: 100, objectFit: "cover" }} />
                <p>
                    <span>{product.quantity} x </span> {product.name}
                </p>
                <p>Price: ${product.price}</p>
            </div>
        ))}
    </Modal.Body>
    <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
            Close
        </Button>
    </Modal.Footer>
</Modal>
        </>
    );
}

export default OrdersAdminPage;
