    //                     <Badge bg="success">Shipped</Badge>
    //                 )}
    //             </td>
    //             <td>
    //                 <span style={{ cursor: "pointer" }} onClick={() => showOrder(products)}>
    //                     View order <i className="fa fa-eye"></i>
    //                 </span>
    //             </td>
    //         </tr>
    //     );
    // }

    return (
        <>

<Table responsive striped bordered hover>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Client Name</th>
                        <th>Items</th>
                        {/* <th>Status</th>
                        <th>Date</th> */}
                        <th>Order Total</th>
                        <th>Address</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.map((order) => (
                        <tr>
                            <td>{order._id}</td>
                            <td>{order.owner?.name}</td>
                            <td>{order.count}</td>
                            <td>${order.total}</td>
                            <td>{order.address}</td>
                            <td>
                               {order.status==="processing"?<Button size="sm" onClick={()=>markShipped(order._id,owner)}>Mark as Shipped</Button>:<Badge bg="success">Shipped</Badge>}
                            </td>
                            <td>{order.date}</td>
                          
                        </tr>
                    ))}
                </tbody>
                
            </Table>
          
            {/* <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Order details</Modal.Title>
                </Modal.Header>
                {orderToShow.map((order) => (
                    <div className="order-details__container d-flex justify-content-around py-2">
                        <img src={order.pictures[0].url} style={{ maxWidth: 100, height: 100, objectFit: "cover" }} />
                        <p>
                            <span>{order.count} x </span> {order.name}
                        </p>
                        <p>Price: ${Number(order.price) * order.count}</p>
                    </div>
                ))}
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close   
                    </Button>
                </Modal.Footer>
            </Modal> */}
        </>
    );
}

export default OrdersAdminPage;
