import React, { useState } from "react";
import { Button, Col, Container, Form, Row, Alert } from "react-bootstrap";
import { Link, useHistory ,useNavigate} from "react-router-dom";
import { useLoginMutation } from "../services/appApi";

function Login() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const history = useHistory(); // Access the history object
    const navigate = useNavigate();
    const [login, { isError, isLoading, error }] = useLoginMutation();

    async function handleLogin(e) {
        e.preventDefault();
        try {
            // Check if the user exists in the database
            const userExists = await checkUserExists(email);
            if (userExists) {
                // If user exists, attempt login
                await login({ email, password });
                // If login is successful, navigate to Home page
                history.push("/");
            } else {
                // If user doesn't exist, show error message or take appropriate action
                console.error("User does not exist.");
            }
        } catch (error) {
            console.error("Login error:", error);
        }
    }

    async function checkUserExists(email) {
        // Perform a database query or API call to check if the user exists
        // Example: You can use fetch or axios to send a request to your backend
        // Replace this with your actual implementation
        // For demonstration, let's assume the checkUserExists function is implemented elsewhere
        // and returns a boolean indicating whether the user exists in the database
        const response = await fetch(`/api/checkUserExists?email=${email}`);
        const data = await response.json();
        return data.exists; // Assuming the response contains a boolean field "exists"
    }
   
    return (
        <Container>
            <Row>
                <Col md={6} className="login__form--container">
                    <Form style={{ width: "100%" }} onSubmit={handleLogin}>
                        <h1>Login to your account</h1>
                        {isError && <Alert variant="danger">{error.data}</Alert>}
                      
                        <Form.Group>
                            <Form.Label>Email Address</Form.Label>
                            <Form.Control type="email" placeholder="Enter email" value={email} required onChange={(e) => setEmail(e.target.value)} />
                        </Form.Group>

                        <Form.Group className="mb-3">
                            <Form.Label>Password</Form.Label>
                            <Form.Control type="password" placeholder="Enter Password" value={password} required onChange={(e) => setPassword(e.target.value)} />
                        </Form.Group>

                        <Form.Group>
                            <Button type="submit" disabled={isLoading}>
                                Login
                            </Button>
                        </Form.Group>

                        <p className="pt-3 text-center">
                            Don't have an account? <Link to="/signup">Create account</Link>{" "}
                        </p>
                    </Form>
                </Col>
                <Col md={6} className="login__image--container"></Col>
            </Row>
        </Container>
    );
}

export default Login;
